/* Variables for consistent colors */ :root { –dark-grey: #333333; –medium-grey: #444444; –light-grey: #f5f5f5; –white: #ffffff; –teal: #4ecdc4; –coral: #ff6b6b; –navy: #1a535c; }
/* Base styling */ body { font-family: ‘Raleway’, sans-serif; color: var(–dark-grey); line-height: 1.7; background-color: var(–light-grey); }
/* Navbar styling */ .navbar { background-color: var(–medium-grey) !important; }
.navbar-brand, .nav-link { color: var(–white) !important; }
.nav-link:hover { color: var(–teal) !important; }
/* Heading styles */ h1, h2, h3, h4, h5, h6 { color: var(–dark-grey); }
h1 { font-size: 2.5em; border-bottom: 2px solid var(–teal); padding-bottom: 0.2em; }
h2 { font-size: 2em; color: var(–medium-grey); }
/* Link styling */ a { color: var(–teal); text-decoration: none; transition: color 0.2s ease; }
a:hover { color: var(–coral); text-decoration: underline; }
/* Quote styling */ blockquote { border-left: 4px solid var(–teal); padding-left: 1em; color: var(–medium-grey); }
/* Content blocks */ .content-block { padding: 2em; margin-bottom: 2em; background-color: var(–white); border-radius: 5px; box-shadow: 0 2px 5px rgba(0,0,0,0.1); }
/* Skills section styling */ .skills-container { display: flex; flex-wrap: wrap; gap: 1.5em; margin-top: 2em; }
.skill-card { background-color: var(–white); border-radius: 5px; border-top: 4px solid var(–teal); padding: 1.5em; flex: 1 1 300px; box-shadow: 0 4px 8px rgba(0,0,0,0.05); transition: transform 0.3s ease; }
.skill-card:hover { transform: translateY(-5px); border-top: 4px solid var(–coral); }
.skill-card h3 { color: var(–medium-grey); border-bottom: 1px solid var(–light-grey); padding-bottom: 0.5em; }
/* Button styling */ .btn-primary { background-color: var(–teal); border-color: var(–teal); color: var(–white); }
.btn-primary:hover { background-color: var(–navy); border-color: var(–navy); }
/* Footer styling */ .footer, footer { background-color: var(–medium-grey); color: var(–white); padding: 2em 0; margin-top: 3em; }
.footer a, footer a { color: var(–teal); }
/* Project cards styling */ .project-card { background-color: var(–white); border-radius: 5px; overflow: hidden; box-shadow: 0 4px 8px rgba(0,0,0,0.1); margin-bottom: 2em; transition: transform 0.3s ease; }
.project-card:hover { transform: translateY(-5px); box-shadow: 0 8px 16px rgba(0,0,0,0.1); }
.project-card img { width: 100%; height: 200px; object-fit: cover; }
.project-card-content { padding: 1.5em; }
.project-card h3 { margin-top: 0; color: var(–medium-grey); }
.project-technologies { display: flex; flex-wrap: wrap; margin-top: 1em; }
/* Contact page styling */ .contact-info { background-color: var(–white); border-radius: 5px; padding: 2em; box-shadow: 0 4px 8px rgba(0,0,0,0.1); margin: 2em 0; }
.contact-item { margin-bottom: 1rem; display: flex; align-items: center; }
.contact-item i { margin-right: 1rem; color: var(–teal); font-size: 1.5rem; width: 2rem; text-align: center; }
.contact-item span { font-weight: bold; margin-right: 0.5rem; width: 5rem; }
/* Experience page styling */ .experience-item { background-color: var(–white); border-radius: 5px; padding: 1.5em; margin-bottom: 1.5em; box-shadow: 0 4px 8px rgba(0,0,0,0.05); position: relative; }
.experience-item::before { content: ’’; position: absolute; left: -20px; top: 50%; width: 20px; height: 2px; background-color: var(–teal); }
.experience-date { color: var(–teal); font-weight: bold; }
.experience-position { font-size: 1.3em; font-weight: bold; margin: 0.5em 0; }
.experience-company { color: var(–medium-grey); margin-bottom: 1em; }
/* Additional elements */ .badge { background-color: var(–teal); color: var(–white); padding: 0.3em 0.6em; border-radius: 3px; font-size: 0.8em; margin: 0.2em; display: inline-block; }
.badge-secondary { background-color: var(–coral); }
.badge-tertiary { background-color: var(–navy); }
/* Highlight text */ .highlight { color: var(–teal); font-weight: bold; }
/* Table styling */ table { width: 100%; border-collapse: collapse; margin: 2em 0; background-color: var(–white); }
th { background-color: var(–medium-grey); color: var(–white); padding: 0.75em; text-align: left; }
td { padding: 0.75em; border-bottom: 1px solid var(–light-grey); }
tr:hover { background-color: rgba(78, 205, 196, 0.1); }
/* Responsive design */ @media (max-width: 768px) { .about-me-container { flex-direction: column-reverse; }
.skills-container { flex-direction: column; }
.contact-item { flex-direction: column; align-items: flex-start; }
.contact-item i { margin-bottom: 0.5rem; } }

